from urllib import requestfrom fastapi import FastAPI, HTTPExceptionfrom math_functions  import add, sub, mul, divfrom pydantic import BaseModel# standard library importimport random # generate random numbers like int float etc.app = FastAPI()class Numbers(BaseModel):    int1: float    int2: float@app.get("/")def root():  # await - waiting for the order to receive it    int1 = random.randint(0, 10)    int2 = random.randint(11, 30)    return {"message": "Hello World",            "int1 =": int1, "int2 =": int2,            "Add":  add(int1, int2),            "Subtract": sub(int1, int2),            "Multiply": mul(int1, int2),            "Divide": div(int1, int2)}@app.get("/health")# check if system is working properlyasync def health():    return {"status": "ok"}@app.post("/add")def add_endpoint(numbers: Numbers):    return {"result": add(numbers.int1, numbers.int2)}@app.post("/sub")def sub_endpoint(numbers: Numbers):    return {"result": sub(numbers.int1, numbers.int2)}@app.post("/mul")def mul_endpoint(numbers: Numbers):    return {"result": mul(numbers.int1, numbers.int2)}@app.post("/div")def div_endpoint(numbers: Numbers):    return {"result": div(numbers.int1, numbers.int2)}